import binascii
import base64

#binary_string = ""
binary_string = ""
hex_string = hex(int(binary_string, 2))[2:] # konversi binary ke hex
base32_string = base64.b32encode(binascii.unhexlify(hex_string)).decode() # konversi hex ke base32
rot13_string = base32_string.encode('rot13') # rotasi base32 dengan ROT13

print(rot13_string)
